// src/App.js

import React, { useEffect, useState } from 'react';
import './App.css';
import profile from './profile.jpg'; // Ensure this is the correct path to your profile image

function App() {
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000); // Change every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div className="sliding-name-background">
          <div className="name-slide">AVI DESAI • AVI DESAI • AVI DESAI • AVI DESAI • AVI DESAI</div>
        </div>
        <div className="hero-container">
          <img src={profile} alt="Avi" className="profile-picture" />
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
        </div>
        <div className="floating-shapes">
          <div className="shape-sphere"></div>
          <div className="shape-cylinder"></div>
          <div className="shape-cube"></div>
          <div className="shape-prism"></div>
        </div>
      </header>
    </div>
  );
}

export default App;
