// src/components/Particles.js
import React, { useEffect, useRef } from 'react';
import './Particles.css';

const Particles = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particlesArray = [];
    const mouse = {
      x: null,
      y: null,
    };

    // Mouse Event Listener
    window.addEventListener('mousemove', (event) => {
      mouse.x = event.x;
      mouse.y = event.y;
    });

    class Particle {
      constructor(x, y, layer) {
        this.x = x;
        this.y = y;
        this.layer = layer; // Assign each particle a specific layer (0-4)
        this.size = Math.random() * 20 + 10; // Initial size between 10 and 30 for better visibility
        this.baseSize = this.size; // Base size to help with pulsing
        this.orbitRadius = Math.random() * 50 + 50; // Random orbit radius between 50 and 100
        this.angle = Math.random() * Math.PI * 2; // Random initial angle for orbit
        this.velocityX = Math.random() * 2 - 1; // Random X velocity for unpredictable motion
        this.velocityY = Math.random() * 2 - 1; // Random Y velocity for unpredictable motion
        this.color = `hsl(${Math.random() * 360}, 100%, 60%)`; // Brighter colors for vivid effect
        this.pulseSpeed = Math.random() * 0.05 + 0.02; // Speed of pulsing effect
      }

      draw() {
        // Create a radial gradient to simulate a 3D effect
        const gradient = context.createRadialGradient(
          this.x - this.size / 4,
          this.y - this.size / 4,
          this.size / 5,
          this.x,
          this.y,
          this.size
        );
        gradient.addColorStop(0, 'white');
        gradient.addColorStop(0.3, this.color);
        gradient.addColorStop(1, 'black');

        context.beginPath();
        context.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        context.fillStyle = gradient;
        context.fill();
        context.closePath();
      }

      update() {
        // Pulsing effect to make particles "breathe"
        this.size = this.baseSize + Math.sin(Date.now() * this.pulseSpeed) * 5;

        // If the mouse is present, make particles orbit around it
        if (mouse.x !== null && mouse.y !== null) {
          this.angle += 0.05; // Constantly change angle to simulate orbit
          const dx = mouse.x - this.x;
          const dy = mouse.y - this.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const force = 0.05; // Attraction force

          // Attraction towards mouse while also applying orbit dynamics
          const targetX = mouse.x + Math.cos(this.angle) * this.orbitRadius;
          const targetY = mouse.y + Math.sin(this.angle) * this.orbitRadius;

          const accelX = (targetX - this.x) * force;
          const accelY = (targetY - this.y) * force;

          this.velocityX += accelX;
          this.velocityY += accelY;

          // Apply velocity
          this.x += this.velocityX;
          this.y += this.velocityY;
        }

        // Slow down slightly to give smooth flowing effect
        this.velocityX *= 0.96;
        this.velocityY *= 0.96;

        // Update position with unpredictable floating movement
        this.x += Math.cos(this.angle) * 0.5;
        this.y += Math.sin(this.angle) * 0.5;

        // Ensure particles stay within canvas bounds
        if (this.x + this.size > canvas.width || this.x - this.size < 0) {
          this.velocityX = -this.velocityX;
        }
        if (this.y + this.size > canvas.height || this.y - this.size < 0) {
          this.velocityY = -this.velocityY;
        }

        this.draw();
      }
    }

    // Initialize Particles with 5 Layers
    function init() {
      particlesArray.length = 0;
      const numParticles = 75; // Number of particles
      const numLayers = 5; // Number of layers

      for (let i = 0; i < numParticles; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const layer = Math.floor(Math.random() * numLayers); // Random layer between 0 and 4
        particlesArray.push(new Particle(x, y, layer));
      }
    }

    // Animation Loop
    function animate() {
      context.clearRect(0, 0, canvas.width, canvas.height);
      particlesArray.forEach(particle => particle.update());
      requestAnimationFrame(animate);
    }

    init();
    animate();

    // Resize canvas and re-initialize particles on window resize
    window.addEventListener('resize', () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      init();
    });
  }, []);

  return (
    <canvas ref={canvasRef} className="particles-canvas"></canvas>
  );
};

export default Particles;
