import React, { useEffect } from 'react';
import './Bio.css';

const Bio = () => {
  useEffect(() => {
    const handleScroll = () => {
      const slides = document.querySelectorAll('.bio-slide');
      slides.forEach((slide) => {
        const slideTop = slide.getBoundingClientRect().top;
        if (slideTop < window.innerHeight - 100) {
          slide.classList.add('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section id="bio" className="bio">
      <div className="bio-slide">
        <div className="shape shape-left"></div>
        <div className="bio-text">
          <p>Bio1: [Write a sentence or two here]</p>
        </div>
        <div className="shape shape-right"></div>
      </div>
      <div className="bio-slide">
        <div className="shape shape-left"></div>
        <div className="bio-text">
          <p>Bio2: [Write a sentence or two here]</p>
        </div>
        <div className="shape shape-right"></div>
      </div>
      <div className="bio-slide">
        <div className="shape shape-left"></div>
        <div className="bio-text">
          <p>Bio3: [Write a sentence or two here]</p>
        </div>
        <div className="shape shape-right"></div>
      </div>
    </section>
  );
};

export default Bio;
