// src/components/Particles.js
import React, { useEffect, useRef } from 'react';
import './Particles.css';

const Particles = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particlesArray = [];
    const mouse = {
      x: undefined,
      y: undefined,
    };

    // Mouse Event
    window.addEventListener('mousemove', (event) => {
      mouse.x = event.x;
      mouse.y = event.y;
    });

    class Particle {
      constructor(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z; // Adding depth for the 3D effect
        this.size = Math.random() * 8 + 1;
        this.baseX = x;
        this.baseY = y;
        this.baseZ = z;
        this.color = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.8)`;
        this.speed = Math.random() * 3 + 1;
      }

      draw() {
        // Simulating depth by adjusting size based on z value
        const scaleFactor = 1 + (this.z / 100);
        const adjustedSize = this.size * scaleFactor;

        context.beginPath();
        context.arc(this.x, this.y, adjustedSize, 0, Math.PI * 2);
        context.fillStyle = this.color;
        context.fill();
        context.closePath();
      }

      update() {
        const dx = mouse.x - this.x;
        const dy = mouse.y - this.y;
        const dz = this.baseZ - this.z; // Depth adjustment
        const distance = Math.sqrt(dx * dx + dy * dy + dz * dz);
        const forceDirectionX = dx / distance;
        const forceDirectionY = dy / distance;
        const forceDirectionZ = dz / distance;
        const maxDistance = 200;
        const force = (maxDistance - distance) / maxDistance;

        if (distance < maxDistance) {
          // Attraction to the mouse position
          this.x += forceDirectionX * this.speed * force;
          this.y += forceDirectionY * this.speed * force;
          this.z += forceDirectionZ * this.speed * force;
        } else {
          // Return to base position if far away from the cursor
          if (this.x !== this.baseX) {
            this.x -= (this.x - this.baseX) / 20;
          }
          if (this.y !== this.baseY) {
            this.y -= (this.y - this.baseY) / 20;
          }
          if (this.z !== this.baseZ) {
            this.z -= (this.z - this.baseZ) / 20;
          }
        }
        this.draw();
      }
    }

    // Initialize Particles
    function init() {
      particlesArray.length = 0;
      const numParticles = 80; // Increased number for more density
      for (let i = 0; i < numParticles; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const z = Math.random() * 100 - 50; // Setting z position between -50 and 50 for depth effect
        particlesArray.push(new Particle(x, y, z));
      }
    }

    // Animation Loop
    function animate() {
      context.clearRect(0, 0, canvas.width, canvas.height);
      particlesArray.forEach(particle => particle.update());
      requestAnimationFrame(animate);
    }

    init();
    animate();

    // Resize canvas and re-initialize particles on window resize
    window.addEventListener('resize', () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      init();
    });
  }, []);

  return (
    <canvas ref={canvasRef} className="particles-canvas"></canvas>
  );
};

export default Particles;
