import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import Navbar from './components/Navbar'; // Import Navbar
import * as THREE from 'three';
import profile from './profile.jpg';
import { scrollSpy } from 'react-scroll'; // Import scrollSpy for navbar

function App() {
  const mountRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Ensure scrollSpy updates correctly to activate navbar links
    scrollSpy.update();
  }, []);

  useEffect(() => {
    const currentMount = mountRef.current;

    // Scene, Camera, Renderer setup for Three.js
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 800);

    const renderer = new THREE.WebGLRenderer({ alpha: true, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 0); // Transparent background
    currentMount.appendChild(renderer.domElement);

    // Light setup
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.9);
    scene.add(ambientLight);

    const pointLight = new THREE.PointLight(0xffffff, 0.6);
    pointLight.position.set(200, 300, 400);
    scene.add(pointLight);

    // Geometries for 3D shapes
    const geometries = [
      new THREE.SphereGeometry(45, 32, 32),
      new THREE.ConeGeometry(45, 70, 4),
      new THREE.ConeGeometry(45, 90, 32),
      new THREE.BoxGeometry(70, 70, 70),
      new THREE.TorusGeometry(35, 15, 16, 100),
      new THREE.ExtrudeGeometry(new THREE.Shape().moveTo(0, 0).lineTo(60, 0).lineTo(30, 60).lineTo(0, 0), { depth: 40, bevelEnabled: true, bevelThickness: 5, bevelSize: 5, bevelSegments: 10 })
    ];

    // Colors for shapes
    const colors = [
      0xffa500,
      0xbb66ff,
      0x6699ff,
      0x87cefa,
      0x90ee90,
      0xffff66
    ];

    const meshes = geometries.map((geometry, index) => {
      const material = new THREE.MeshPhongMaterial({ color: colors[index] });
      const mesh = new THREE.Mesh(geometry, material);

      const positions = [
        { x: -400, y: 200, z: 100 },
        { x: 400, y: 200, z: 100 },
        { x: -500, y: 0, z: 100 },
        { x: 500, y: 0, z: 100 },
        { x: -400, y: -200, z: 100 },
        { x: 400, y: -200, z: 100 },
      ];

      const position = positions[index % positions.length];
      mesh.position.set(position.x, position.y, position.z);

      scene.add(mesh);
      return mesh;
    });

    // Animate shapes for floating effect
    let time = 0;

    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.01;

      meshes.forEach((mesh, index) => {
        const initialY = mesh.position.y;
        mesh.position.y = initialY + Math.sin(time + index) * 5;
      });

      renderer.render(scene, camera);
    };
    animate();

    // Cleanup function
    return () => {
      if (currentMount) {
        currentMount.removeChild(renderer.domElement);
      }
    };
  }, []);

  return (
    <div className="App">
      <Navbar /> {/* Adding Navbar here */}
      <header id="hero" className="App-header">
        <div ref={mountRef} className="floating-shapes"></div>
        <div className="sliding-name-background">
          <div className="name-slide">
            Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai
          </div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
        </div>
      </header>

      <main className="App-main">
        <section id="bio" className="bio-section">
          <h2>Bio</h2>
          <p>
            Passionate software engineer with extensive experience in frontend development, creating seamless and visually engaging user experiences.
          </p>
        </section>
        <section id="experience" className="experience-section">
          <h2>Experience</h2>
          <p>Details about professional experience here.</p>
        </section>
        <section id="projects" className="projects-section">
          <h2>Projects</h2>
          <p>Details about projects here.</p>
        </section>
      </main>
    </div>
  );
}

export default App;
