import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import * as THREE from 'three';
import profile from './profile.jpg';
import { Events, scrollSpy } from 'react-scroll';

function App() {
  const mountRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const currentMount = mountRef.current;

    // Scene, Camera, Renderer setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 800);

    const renderer = new THREE.WebGLRenderer({ alpha: true, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 0); // Transparent background
    currentMount.appendChild(renderer.domElement);

    // Light setup for a soft and inviting effect
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.9);
    scene.add(ambientLight);

    const pointLight = new THREE.PointLight(0xffffff, 0.6);
    pointLight.position.set(200, 300, 400);
    scene.add(pointLight);

    // Geometries for 3D shapes with updated sizes
    const geometries = [
      new THREE.SphereGeometry(45, 32, 32), // Sphere with radius 45
      new THREE.ConeGeometry(45, 70, 4), // Pyramid with radius 45, height 70 (taller)
      new THREE.ConeGeometry(45, 90, 32), // Cone with radius 45, height 90
      createRoundedBox(70, 70, 70, 8, 16), // Custom rounded box slightly smaller (dimensions 70x70x70)
      new THREE.TorusGeometry(35, 15, 16, 100), // Torus with main radius 35, tube radius 15
      createRoundedStar(60, 30, 5, 40, 3), // Star with larger dimensions for bigger appearance
    ];

    // Define specified lighter colors for each shape
    const colors = [
      0xffa500, // Lighter Orange
      0xbb66ff, // Lighter Purple
      0x6699ff, // Lighter Blue
      0x87cefa, // Sky Blue
      0x90ee90, // Light Green
      0xffff66  // Light Yellow
    ];

    // Store meshes for animation
    const meshes = geometries.map((geometry, index) => {
      const material = new THREE.MeshPhongMaterial({ color: colors[index] });
      const mesh = new THREE.Mesh(geometry, material);

      // Use the specified original positions
      const positions = [
        { x: -400, y: 200, z: 100 },  // Top left (Sphere)
        { x: 400, y: 200, z: 100 },   // Top right (Pyramid)
        { x: -500, y: 0, z: 100 },    // Middle left (Cone)
        { x: 500, y: 0, z: 100 },     // Middle right (Rounded Box)
        { x: -400, y: -200, z: 100 }, // Bottom left (Torus)
        { x: 400, y: -200, z: 100 },  // Bottom right (Star)
      ];

      const position = positions[index % positions.length];
      mesh.position.set(position.x, position.y, position.z);

      scene.add(mesh);
      return mesh;
    });

    // Animate the shapes - floating effect
    let time = 0; // Time variable for calculating sine wave

    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.01; // Slower increment to make floating slower

      meshes.forEach((mesh, index) => {
        // Move the shape up and down in a sinusoidal pattern
        const initialY = mesh.position.y; // Initial Y position
        mesh.position.y = initialY + Math.sin(time + index) * 5; // Slower oscillation by 5 units
      });

      renderer.render(scene, camera);
    };
    animate();

    // Cleanup function
    return () => {
      if (currentMount) {
        currentMount.removeChild(renderer.domElement);
      }
    };
  }, []);

  useEffect(() => {
    // Listen for scroll events to update the active link properly
    Events.scrollEvent.register('begin', function () {
      console.log('Scrolling started');
    });

    Events.scrollEvent.register('end', function () {
      console.log('Scrolling ended');
    });

    // Make sure the scroll spy is updated
    scrollSpy.update();

    return () => {
      // Cleanup scroll events on component unmount
      Events.scrollEvent.remove('begin');
      Events.scrollEvent.remove('end');
    };
  }, []);

  return (
    <div className="App">
      <Navbar /> {/* Add Navbar component here */}
      <header id="hero" className="App-header">
        <div ref={mountRef} className="floating-shapes"></div>
        <div className="sliding-name-background">
          <div className="name-slide">
            Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai
          </div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
        </div>
      </header>

      <main className="App-main">
        <section id="bio" className="bio-section">
          <h2>Bio</h2>
          <p>
            Passionate software engineer with extensive experience in frontend development, creating seamless and visually engaging user experiences.
          </p>
        </section>
      </main>
    </div>
  );
}

export default App;
