// src/components/Navbar.js
import React, { useEffect } from 'react';
import './Navbar.css';

const Navbar = () => {
  useEffect(() => {
    const sections = document.querySelectorAll('section[id], header[id]');
    const navLinks = document.querySelectorAll('.navbar-links a');

    let activeId = null;

    const options = {
      root: null,
      rootMargin: '0px',
      threshold: Array.from({ length: 101 }, (_, i) => i / 100), // Thresholds from 0 to 1 in 0.01 increments
    };

    const observer = new IntersectionObserver((entries) => {
      let maxRatio = 0;
      let visibleSectionId = null;

      entries.forEach((entry) => {
        if (entry.intersectionRatio > maxRatio) {
          maxRatio = entry.intersectionRatio;
          visibleSectionId = entry.target.getAttribute('id');
        }
      });

      if (visibleSectionId && activeId !== visibleSectionId) {
        activeId = visibleSectionId;

        // Remove active class from all links
        navLinks.forEach((link) => {
          link.classList.remove('active');
        });

        // Add active class to the current link
        const currentLink = document.querySelector(
          `.navbar-links a[href="#${activeId}"]`
        );
        if (currentLink) {
          currentLink.classList.add('active');
        }
      }
    }, options);

    sections.forEach((section) => {
      observer.observe(section);
    });

    return () => {
      if (observer) {
        observer.disconnect();
      }
    };
  }, []);

  return (
    <nav className="navbar">
      <ul className="navbar-links">
        <li>
          <a href="#hero">Home</a>
        </li>
        <li>
          <a href="#bio">Bio</a>
        </li>
        <li>
          <a href="#experience">Experience</a>
        </li>
        <li>
          <a href="#projects">Projects</a>
        </li>
        <li>
          <a href="#contact">Contact</a>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
