import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import * as THREE from 'three';
import profile from './profile.jpg';

function App() {
  const mountRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Store the current ref value in a local variable
    const currentMount = mountRef.current;

    // Scene, Camera, Renderer setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 500;

    const renderer = new THREE.WebGLRenderer({ alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 0); // Transparent background
    currentMount.appendChild(renderer.domElement);

    // Lighting
    const light = new THREE.PointLight(0xffffff, 1);
    light.position.set(200, 200, 300);
    scene.add(light);

    // Geometry and Material
    const geometries = [
      new THREE.SphereGeometry(50, 32, 32),
      new THREE.CylinderGeometry(40, 40, 100, 32),
      new THREE.BoxGeometry(80, 80, 80),
      new THREE.ConeGeometry(50, 100, 32)
    ];

    const materials = [
      new THREE.MeshPhongMaterial({ color: 0xff7e5f }),
      new THREE.MeshPhongMaterial({ color: 0xff4b1f }),
      new THREE.MeshPhongMaterial({ color: 0x6a11cb }),
      new THREE.MeshPhongMaterial({ color: 0xf9d423 })
    ];

    // Meshes and adding to scene
    const meshes = geometries.map((geometry, index) => {
      const material = materials[index % materials.length];
      const mesh = new THREE.Mesh(geometry, material);
      mesh.position.x = (Math.random() - 0.5) * 800;
      mesh.position.y = (Math.random() - 0.5) * 800;
      mesh.position.z = (Math.random() - 0.5) * 800;
      scene.add(mesh);
      return mesh;
    });

    // Animation function
    const animate = () => {
      requestAnimationFrame(animate);
      meshes.forEach((mesh) => {
        mesh.rotation.x += 0.01;
        mesh.rotation.y += 0.01;
      });
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup
    return () => {
      currentMount.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div ref={mountRef} className="floating-shapes"></div>
        <div className="sliding-name-background">
          <div className="name-slide">Avi Desai • Avi Desai • Avi Desai</div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
        </div>
      </header>

      <main className="App-main">
        <section className="bio-section">
          <h2>Bio</h2>
          <p>
            Passionate software engineer with extensive experience in frontend development, creating seamless and visually engaging user experiences.
          </p>
        </section>
      </main>
    </div>
  );
}

export default App;
