// src/components/Particles.js
import React, { useEffect, useRef } from 'react';
import './Particles.css';

const Particles = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particlesArray = [];
    const mouse = {
      x: undefined,
      y: undefined,
    };

    // Mouse Event
    window.addEventListener('mousemove', (event) => {
      mouse.x = event.x;
      mouse.y = event.y;
    });

    class Particle {
      constructor(x, y, z, layer) {
        this.x = x;
        this.y = y;
        this.z = z; // Depth for 3D effect
        this.size = Math.random() * 20 + 20; // Larger sizes for better visibility
        this.baseX = x;
        this.baseY = y;
        this.layer = layer; // Assign each particle a specific layer
        this.speedX = Math.random() * 1.5 - 0.75; // Random X speed
        this.speedY = Math.random() * 1.5 - 0.75; // Random Y speed
        this.color = `hsl(${Math.random() * 360}, 100%, 60%)`; // Brighter colors for vivid effect
      }

      draw() {
        // Simulating depth by adjusting gradient for 3D effect
        const gradient = context.createRadialGradient(
          this.x - this.size / 4,
          this.y - this.size / 4,
          this.size / 5,
          this.x,
          this.y,
          this.size
        );
        gradient.addColorStop(0, 'white');
        gradient.addColorStop(0.3, this.color);
        gradient.addColorStop(1, 'black');

        context.beginPath();
        context.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        context.fillStyle = gradient;
        context.fill();
        context.closePath();
      }

      update(particles) {
        // Mouse attraction logic (All particles are attracted to the mouse)
        const dx = mouse.x - this.x;
        const dy = mouse.y - this.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const force = 0.01; // Strength of attraction
        this.x += dx * force;
        this.y += dy * force;

        // Update position with random floating movement
        this.x += this.speedX;
        this.y += this.speedY;

        // Collision detection with other particles on the same layer
        particles.forEach(particle => {
          if (this === particle || this.layer !== particle.layer) return; // Skip self and different layers

          const dx = particle.x - this.x;
          const dy = particle.y - this.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < this.size + particle.size) {
            // Collision response: simple elastic bounce
            const angle = Math.atan2(dy, dx);
            const overlap = (this.size + particle.size) - distance;

            this.x -= Math.cos(angle) * overlap / 2;
            this.y -= Math.sin(angle) * overlap / 2;

            particle.x += Math.cos(angle) * overlap / 2;
            particle.y += Math.sin(angle) * overlap / 2;

            // Adjust speed on collision
            this.speedX = -this.speedX;
            this.speedY = -this.speedY;
            particle.speedX = -particle.speedX;
            particle.speedY = -particle.speedY;
          }
        });

        // Ensure particles stay within bounds
        if (this.x + this.size > canvas.width || this.x - this.size < 0) {
          this.speedX = -this.speedX;
        }
        if (this.y + this.size > canvas.height || this.y - this.size < 0) {
          this.speedY = -this.speedY;
        }

        this.draw();
      }
    }

    // Initialize Particles with 5 Layers
    function init() {
      particlesArray.length = 0;
      const numParticles = 75; // Adjust number for a good visual density
      const numLayers = 5; // We have 5 different layers

      for (let i = 0; i < numParticles; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const z = Math.random() * 50 - 25; // Z position for depth simulation
        const layer = Math.floor(Math.random() * numLayers); // Assign a random layer between 0 and 4
        particlesArray.push(new Particle(x, y, z, layer));
      }
    }

    // Animation Loop
    function animate() {
      context.clearRect(0, 0, canvas.width, canvas.height);
      particlesArray.forEach(particle => particle.update(particlesArray));
      requestAnimationFrame(animate);
    }

    init();
    animate();

    // Resize canvas and re-initialize particles on window resize
    window.addEventListener('resize', () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      init();
    });
  }, []);

  return (
    <canvas ref={canvasRef} className="particles-canvas"></canvas>
  );
};

export default Particles;
