import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import * as THREE from 'three';
import profile from './profile.jpg';

function App() {
  const mountRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    // Flipping text effect
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    // Cleanup for interval
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Adding the Three.js scene for shapes
    const currentMount = mountRef.current;

    // Scene Setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.z = 600;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff); // White background
    currentMount.appendChild(renderer.domElement);

    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
    scene.add(ambientLight);
    const pointLight = new THREE.PointLight(0xffffff, 1);
    pointLight.position.set(200, 200, 200);
    scene.add(pointLight);

    // Create 3D Shapes
    const shapes = [];

    // Rounded Cube
    const cubeGeometry = new THREE.BoxGeometry(100, 100, 100, 10, 10, 10);
    const cubeMaterial = new THREE.MeshStandardMaterial({
      color: 0x6a11cb,
      emissive: 0x2575fc,
      roughness: 0.5,
      metalness: 0.1,
    });
    const cube = new THREE.Mesh(cubeGeometry, cubeMaterial);
    cube.position.set(-200, 50, 0);
    shapes.push(cube);

    // Rounded Cylinder
    const cylinderGeometry = new THREE.CylinderGeometry(50, 50, 150, 32);
    const cylinderMaterial = new THREE.MeshStandardMaterial({
      color: 0xff4b1f,
      emissive: 0x1fddff,
      roughness: 0.6,
      metalness: 0.3,
    });
    const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
    cylinder.position.set(200, -50, 0);
    shapes.push(cylinder);

    // Add all shapes to the scene
    shapes.forEach((shape) => scene.add(shape));

    // Animation Loop
    const animate = function () {
      requestAnimationFrame(animate);
      // Rotate shapes for animation
      shapes.forEach((shape) => {
        shape.rotation.x += 0.005;
        shape.rotation.y += 0.005;
      });
      renderer.render(scene, camera);
    };

    animate();

    // Cleanup on component unmount
    return () => {
      currentMount.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div className="sliding-name-background">
          <div className="name-slide">AVI DESAI • AVI DESAI • AVI DESAI • AVI DESAI</div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
        </div>
        <div ref={mountRef} className="floating-shapes"></div>
      </header>

      <main className="App-main">
        <section className="bio-section">
          <h2>Bio</h2>
          <p>
            Passionate software engineer with extensive experience in frontend development, creating seamless and visually engaging user experiences.
          </p>
        </section>
      </main>
    </div>
  );
}

export default App;
