import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Bio from './components/Bio'; 
import * as THREE from 'three';
import profile from './profile.jpg';
import { scrollSpy } from 'react-scroll';
import { geometries, colors } from './Shapes';

function App() {
  const mountRef = useRef(null);
  const bioRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people", "trying to be a better person"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const handleScrollToBio = () => {
    bioRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="App">
      <Navbar />
      <header className="App-header">
        <div ref={mountRef} className="floating-shapes"></div>
        <div className="sliding-name-background">
          <div className="name-slide">
            Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai • Avi Desai
          </div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
          <button onClick={handleScrollToBio} className="scroll-to-bio-button">Read More About Me</button>
        </div>
      </header>

      <main className="App-main" ref={bioRef}>
        <Bio />
      </main>
    </div>
  );
}

export default App;
