import React, { useEffect, useState } from 'react';
import './Bio.css';

const Bio = () => {
  const [currentSlideIndex, setCurrentSlideIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY + window.innerHeight / 2; // Get middle of the viewport
      const slides = document.querySelectorAll('.bio-slide');
      let newIndex = 0;

      slides.forEach((slide, index) => {
        const slideTop = slide.offsetTop;
        if (scrollPosition > slideTop) {
          newIndex = index;
        }
      });

      setCurrentSlideIndex(newIndex);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section id="bio" className="bio">
      {["Hi there! I’m Avi, a passionate software engineer who loves creating visually engaging user experiences.",
        "I’ve had the opportunity to work on a variety of projects, from product management roles to sales engineering. This diverse background has given me a unique perspective.",
        "My goal is to keep learning and growing, taking on new challenges and building tools that make a real difference. In my spare time, you can find me exploring new tech trends or enjoying a good cup of coffee."
      ].map((text, index) => (
        <div key={index} className={`bio-slide ${index === currentSlideIndex ? 'visible' : ''}`}>
          <div className="bio-text">
            <p>{text}</p>
          </div>
        </div>
      ))}
    </section>
  );
};

export default Bio;
