// src/components/Particles.js
import React, { useEffect, useRef } from 'react';
import './Particles.css';

const Particles = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particlesArray = [];
    const mouse = {
      x: undefined,
      y: undefined,
    };

    // Mouse Event
    window.addEventListener('mousemove', (event) => {
      mouse.x = event.x;
      mouse.y = event.y;
    });

    class Particle {
      constructor(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z; // Adding depth for the 3D effect
        this.size = Math.random() * 15 + 10; // Larger size for visibility
        this.baseX = x;
        this.baseY = y;
        this.baseZ = z;
        this.speedX = Math.random() * 2 - 1; // Random X speed
        this.speedY = Math.random() * 2 - 1; // Random Y speed
        this.color = `hsl(${Math.random() * 360}, 70%, 50%)`; // Using HSL for vibrant colors
      }

      draw() {
        // Simulating depth by adjusting gradient for 3D effect
        const gradient = context.createRadialGradient(
          this.x - this.size / 4,
          this.y - this.size / 4,
          this.size / 5,
          this.x,
          this.y,
          this.size
        );
        gradient.addColorStop(0, 'white');
        gradient.addColorStop(0.3, this.color);
        gradient.addColorStop(1, 'black');

        context.beginPath();
        context.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        context.fillStyle = gradient;
        context.fill();
        context.closePath();
      }

      update(particles) {
        // Mouse attraction logic
        const dx = mouse.x - this.x;
        const dy = mouse.y - this.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const maxDistance = 200;
        const force = (maxDistance - distance) / maxDistance;

        if (distance < maxDistance) {
          this.x -= dx * force * 0.1;
          this.y -= dy * force * 0.1;
        }

        // Update position with random floating movement
        this.x += this.speedX;
        this.y += this.speedY;

        // Collision detection with other particles
        for (let i = 0; i < particles.length; i++) {
          if (this === particles[i]) continue; // Skip self comparison
          const dx = particles[i].x - this.x;
          const dy = particles[i].y - this.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < this.size + particles[i].size) {
            // Collision response: simple elastic bounce
            const angle = Math.atan2(dy, dx);
            const overlap = (this.size + particles[i].size) - distance;

            this.x -= Math.cos(angle) * overlap / 2;
            this.y -= Math.sin(angle) * overlap / 2;

            particles[i].x += Math.cos(angle) * overlap / 2;
            particles[i].y += Math.sin(angle) * overlap / 2;

            // Adjust speed on collision
            this.speedX = -this.speedX;
            this.speedY = -this.speedY;
            particles[i].speedX = -particles[i].speedX;
            particles[i].speedY = -particles[i].speedY;
          }
        }

        // Ensure particles stay within bounds
        if (this.x + this.size > canvas.width || this.x - this.size < 0) {
          this.speedX = -this.speedX;
        }
        if (this.y + this.size > canvas.height || this.y - this.size < 0) {
          this.speedY = -this.speedY;
        }

        this.draw();
      }
    }

    // Initialize Particles
    function init() {
      particlesArray.length = 0;
      const numParticles = 60; // Adjust number of particles for visibility and collision handling
      for (let i = 0; i < numParticles; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const z = Math.random() * 50 - 25; // Z position for depth simulation
        particlesArray.push(new Particle(x, y, z));
      }
    }

    // Animation Loop
    function animate() {
      context.clearRect(0, 0, canvas.width, canvas.height);
      particlesArray.forEach(particle => particle.update(particlesArray));
      requestAnimationFrame(animate);
    }

    init();
    animate();

    // Resize canvas and re-initialize particles on window resize
    window.addEventListener('resize', () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      init();
    });
  }, []);

  return (
    <canvas ref={canvasRef} className="particles-canvas"></canvas>
  );
};

export default Particles;
