import React, { useEffect } from 'react';
import './Bio.css';

const Bio = () => {
  useEffect(() => {
    const handleScroll = () => {
      const slides = document.querySelectorAll('.bio-slide');
      slides.forEach((slide, index) => {
        const slideTop = slide.getBoundingClientRect().top;
        if (slideTop < window.innerHeight - 100) {
          slide.classList.add('visible');
        } else {
          slide.classList.remove('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section id="bio" className="bio">
      <div className="bio-slide">
        <div className="bio-text">
          <p>Hi there! I’m Avi, a passionate software engineer who loves creating visually engaging user experiences. My journey in tech started with building small web apps, and it has evolved into solving complex front-end challenges.</p>
        </div>
      </div>
      <div className="bio-slide">
        <div className="bio-text">
          <p>I’ve had the opportunity to work on a variety of projects, from product management roles to sales engineering. This diverse background has given me a unique perspective on how to combine technical skills with stakeholder needs to build impactful products.</p>
        </div>
      </div>
      <div className="bio-slide">
        <div className="bio-text">
          <p>My goal is to keep learning and growing as a developer and product manager, taking on new challenges and building tools that make a real difference. In my spare time, you can find me exploring new tech trends or enjoying a good cup of coffee.</p>
        </div>
      </div>
    </section>
  );
};

export default Bio;
