import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import * as THREE from 'three';
import profile from './profile.jpg';

function App() {
  const mountRef = useRef(null);
  const [flippingText, setFlippingText] = useState("trying to build cool stuff");

  useEffect(() => {
    const texts = ["trying to build cool stuff", "trying to meet new people"];
    let index = 0;
    const interval = setInterval(() => {
      index = (index + 1) % texts.length;
      setFlippingText(texts[index]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const currentMount = mountRef.current;

    // Scene, Camera, Renderer setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 800);

    const renderer = new THREE.WebGLRenderer({ alpha: true, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 0); // Transparent background
    currentMount.appendChild(renderer.domElement);

    // Light setup for a soft and inviting effect
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.9);
    scene.add(ambientLight);

    const pointLight = new THREE.PointLight(0xffffff, 0.6);
    pointLight.position.set(200, 300, 400);
    scene.add(pointLight);

    // Custom Rounded Box Geometry
    const createRoundedBox = (width, height, depth, radius, smoothness) => {
      const shape = new THREE.Shape();
      const halfWidth = width / 2 - radius;
      const halfHeight = height / 2 - radius;

      shape.moveTo(-halfWidth, halfHeight);
      shape.lineTo(halfWidth, halfHeight);
      shape.quadraticCurveTo(width / 2, halfHeight, width / 2, halfHeight - radius);
      shape.lineTo(width / 2, -halfHeight);
      shape.quadraticCurveTo(width / 2, -height / 2, halfWidth, -height / 2);
      shape.lineTo(-halfWidth, -height / 2);
      shape.quadraticCurveTo(-width / 2, -height / 2, -width / 2, -halfHeight);
      shape.lineTo(-width / 2, halfHeight - radius);
      shape.quadraticCurveTo(-width / 2, halfHeight, -halfWidth, halfHeight);

      const extrudeSettings = {
        depth: depth,
        bevelEnabled: true,
        bevelSegments: smoothness,
        steps: 2,
        bevelSize: radius,
        bevelThickness: radius,
      };

      return new THREE.ExtrudeGeometry(shape, extrudeSettings);
    };

    // Custom Cylinder Geometry with Rounded Edges
    const createRoundedCylinder = (topRadius, bottomRadius, height, radialSegments, heightSegments) => {
      const geometry = new THREE.CylinderGeometry(topRadius, bottomRadius, height, radialSegments, heightSegments);
      geometry.translate(0, height / 2, 0); // Translate for rounded bevel effects
      return geometry;
    };

    // Shape materials with softer, gradient colors
    const materials = [
      new THREE.MeshPhongMaterial({ color: 0xffae42, shininess: 50 }),
      new THREE.MeshPhongMaterial({ color: 0x42a1ff, shininess: 50 }),
      new THREE.MeshPhongMaterial({ color: 0x66ff66, shininess: 50 }),
      new THREE.MeshPhongMaterial({ color: 0xff66cc, shininess: 50 }),
      new THREE.MeshPhongMaterial({ color: 0xffff66, shininess: 50 }),
    ];

    // Geometries for 3D shapes with updated cylinder
    const geometries = [
      new THREE.SphereGeometry(50, 64, 64), // Sphere with increased segments
      createRoundedCylinder(20, 20, 100, 64, 32), // Updated Cylinder with rounded edges
      new THREE.DodecahedronGeometry(40, 1), // Dodecahedron with some refinement
      new THREE.TorusGeometry(35, 15, 30, 100), // Torus with increased segments for smoother edges
      new THREE.ConeGeometry(40, 80, 32), // Cone with more segments
      createRoundedBox(70, 70, 70, 10, 16), // Custom rounded box
    ];

    const meshes = geometries.map((geometry, index) => {
      const material = materials[index % materials.length];
      const mesh = new THREE.Mesh(geometry, material);

      // Custom positions for each shape to align with the edges of the screen
      const positions = [
        { x: -450, y: 250, z: 100 },  // Top left
        { x: 450, y: 250, z: 100 },   // Top right
        { x: -450, y: -300, z: 100 }, // Bottom left
        { x: 450, y: -300, z: 100 },  // Bottom right
        { x: 0, y: -400, z: 150 },    // Bottom center
        { x: -500, y: 50, z: 100 },   // Middle left
        { x: 500, y: 50, z: 100 },    // Middle right
      ];

      const position = positions[index % positions.length];
      mesh.position.set(position.x, position.y, position.z);

      scene.add(mesh);
      return mesh;
    });

    // Animate the shapes
    const animate = () => {
      requestAnimationFrame(animate);
      meshes.forEach((mesh) => {
        mesh.rotation.x += 0.002;
        mesh.rotation.y += 0.002;

        // Gradient color effect based on rotation (slower color change)
        const hue = (mesh.rotation.x * 0.05 + mesh.rotation.y * 0.05) % 1;
        mesh.material.color.setHSL(hue, 0.7, 0.6);
      });
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup function
    return () => {
      if (currentMount) {
        currentMount.removeChild(renderer.domElement);
      }
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div ref={mountRef} className="floating-shapes"></div>
        <div className="sliding-name-background">
          <div className="name-slide">Avi Desai • Avi Desai • Avi Desai</div>
        </div>
        <div className="hero-container">
          <div className="intro-text">
            <span className="greeting-text">Hi, I'm</span> <span className="name-text">Avi!</span>
          </div>
          <p className="flipping-text">{flippingText}</p>
          <img src={profile} alt="Avi" className="profile-picture" />
        </div>
      </header>

      <main className="App-main">
        <section className="bio-section">
          <h2>Bio</h2>
          <p>
            Passionate software engineer with extensive experience in frontend development, creating seamless and visually engaging user experiences.
          </p>
        </section>
      </main>
    </div>
  );
}

export default App;
