// src/components/Navbar.js
import React, { useEffect } from 'react';
import './Navbar.css';

const Navbar = () => {
  useEffect(() => {
    // Function to update the active link based on scroll position
    const handleScroll = () => {
      const sections = document.querySelectorAll('section, header');
      const scrollPos = window.scrollY + 100; // Adjust offset as needed

      sections.forEach((section) => {
        const id = section.getAttribute('id');
        if (id) {
          const offsetTop = section.offsetTop;
          const offsetHeight = section.offsetHeight;

          if (scrollPos >= offsetTop && scrollPos < offsetTop + offsetHeight) {
            document.querySelector('.navbar-links a.active')?.classList.remove('active');
            document.querySelector(`.navbar-links a[href="#${id}"]`)?.classList.add('active');
          }
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    // Trigger once on mount
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className="navbar">
      <ul className="navbar-links">
        <li>
          <a href="#hero">Home</a>
        </li>
        <li>
          <a href="#bio">Bio</a>
        </li>
        <li>
          <a href="#experience">Experience</a>
        </li>
        <li>
          <a href="#projects">Projects</a>
        </li>
        <li>
          <a href="#contact">Contact</a>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
