import React from 'react';
import './Bio.css';
import profile1 from '/Users/avi/Desktop/mywebsite/personal-website/src/profile.jpg';
import profile2 from '/Users/avi/Desktop/mywebsite/personal-website/src/profile.jpg';
import profile3 from '/Users/avi/Desktop/mywebsite/personal-website/src/Gradpic.jpeg';

const Bio = () => {
  const images = [profile1, profile2, profile3];

  return (
    <section id="bio" className="bio">
      <div className="bio-content">
        <div className="bio-text">
          <h2>About Me</h2>
          <p>
            Hi! I’m Avi, a passionate software engineer dedicated to creating visually engaging and functional user experiences. My journey started with small web applications, but now I'm taking on complex front-end challenges and loving every moment of it. 
          </p>
          <p>
            With a background that spans product management and sales engineering, I bridge the gap between technology and business needs. I'm always looking for ways to grow, learn, and create solutions that make a real impact.
          </p>
          <p>
            Let’s connect—whether it's about technology, new trends, or just enjoying a good cup of coffee!
          </p>
        </div>
        <div className="bio-image">
          {images.map((image, index) => (
            <img 
              key={index}
              src={image}
              alt={`Avi ${index + 1}`}
              className={`bio-photo bio-photo-${index}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Bio;
